mapper.insert:
INSERT INTO BOARD_IMAGES (BIMGNO, BNO, MNO, ORIGINNAME, CHANGENAME, IMGPATH, IMGLEVEL, UPLOADDATE) SELECT SEQ_BOARD_IMAGE.NEXTVAL, A.* from(
SELECT #{bi.bno}, #{bi.mno}, #{bi.originname}, #{bi.changename}, #{bi.imgpath}, #{bi.imglevel}, SYSDATE FROM DUAL
) A

mapper.select:
SELECT COUNT(*)
FROM "LIKE"
WHERE BNO = #{bno} AND MNO = #{mno}

mapper.update:
UPDATE BOARD SET BTITLE = #{btitle}, BCONTENT =
#{bcontent}
WHERE BNO = #{bno}

mapper.select:

SELECT RNO, BNO, MNO
FROM BOARD_REPLY
WHERE RNO = #{rno}
