mapper.insert:
INSERT INTO STYBOARD_IMAGES (BIMGNO, BNO, STYNO, ORIGINNAME, CHANGENAME, IMGPATH, IMGLEVEL, UPLOADDATE) SELECT SEQ_STYBOARD_IMAGE.NEXTVAL, A.* from(
SELECT #{bi.bno}, #{bi.styno}, #{bi.originname}, #{bi.changename}, #{bi.imgpath}, #{bi.imglevel}, SYSDATE FROM DUAL
) A

